descriptor.controllerScript:
self.onInit = function() {
    var scope = self.ctx.$scope;
    var id = self.ctx.$scope.$injector.get('utils').guid();
    scope.tableId = "table-"+id;
    scope.ctx = self.ctx;
}

self.onDataUpdated = function() {
    self.ctx.$scope.$broadcast('entities-table-data-updated', self.ctx.$scope.tableId);
}

self.typeParameters = function() {
    return {
        maxDatasources: 1,
        dataKeysOptional: true
    };
}

self.actionSources = function() {
    return {
        'actionCellButton': {
            name: 'widget-action.action-cell-button',
            multiple: true
        },
        'rowClick': {
            name: 'widget-action.row-click',
            multiple: false
        },
        'rowDoubleClick': {
            name: 'widget-action.row-double-click',
            multiple: false
        }
    };
}

self.onDestroy = function() {
}

